    $(function() {
        $('.myScroll').myScroll({
          speed: 40,
          //数值越大，速度越慢
          rowHeight: 58 //li的高度
        });

        $('.notice-tab li.tab_g').hover(function() {
          //获取当前的索引
          //去掉全部的on class
          $('.notice-tab li').removeClass('on');
          $(this).addClass('on');
          var index = $('.notice-tab li').index(this);
          $('.notice-main .draw-contents').hide();
          $('.notice-main').find('.draw-contents').eq(index).show();
        });
      });
